import sysfrom PyQt5 import QtWidgets, uicfrom util.helpers import *class weatherApp(QtWidgets.QMainWindow):    def __init__(self):        # initialize fixed sized weatherApp instance        super(weatherApp, self).__init__()        uic.loadUi('forms/mainWindow.ui', self).setFixedSize(800, 600)        # display correct days        self.labelToday.setText(getDay().strftime("%A"))        self.labelTomorrow.setText(getDay(daysFromNow=1).strftime("%A"))        self.labelDayAfter.setText(getDay(daysFromNow=2).strftime("%A"))        # call onClick function on button click        self.city = ''        self.submitCity.clicked.connect(self.onClick)    def displayForecast(self, currentWeather):        # display today's forecast        forecastTemp = str(round(currentWeather["main"]["temp_min"] - 273.15, 1)) + "℃" + " / " \                     + str(round(currentWeather["main"]["temp_max"] - 273.15, 1)) + "℃"        self.todaysForecastTemp.setText(forecastTemp)        self.todaysForecastDesc.setText(currentWeather["weather"][0]["main"])        # get forecast weather report        forecastWeatherReport = getCityWeather(self.city, forecast=True)        # display tomorrow's forecast        tempMin, tempMax, weatherDesc = getWeatherForecastByDay(self.city, daysFromNow=1)        forecastTemp = '{} / {}℃'.format(tempMin, tempMax)        self.tomoForecastTemp.setText(forecastTemp)        self.tomoForecastDesc.setText(weatherDesc)        # display day after tomorrow's forecast        tempMin, tempMax, weatherDesc = getWeatherForecastByDay(self.city, daysFromNow=2)        forecastTemp = '{} / {}℃'.format(tempMin, tempMax)        self.dayAftersForecastTemp.setText(forecastTemp)        self.dayAftersForecastDesc.setText(weatherDesc)    def onClick(self):        # get weather report for given city        self.city = self.inputCity.text()        currentWeatherReport = getCityWeather(self.city)        # 'invalid input' handling        if currentWeatherReport is None:            QtWidgets.QMessageBox.about(self, "Can't reach weather report", "Please try again")        else:            # update window title with city name            self.setWindowTitle("weatherApp - " + self.city)            # display labels with current city temperature, weather description            self.cityTemp.setText(str(round(currentWeatherReport["main"]["temp"] - 273.15, 1)) + "℃")            self.weatherDescription.setText(currentWeatherReport["weather"][0]["description"])            # display weather forecast on window            self.displayForecast(currentWeatherReport)def main():    app = QtWidgets.QApplication([])    weatherAppWindow = weatherApp()    weatherAppWindow.show()    sys.exit(app.exec())if __name__ == '__main__':    main()