import sysfrom PyQt5 import QtWidgets, uicfrom PyQt5.QtGui import QPixmapfrom util.helpers import getDay, getCurrentWeather, getWeatherForecastByDay, getWeatherIconclass weatherApp(QtWidgets.QMainWindow):    def __init__(self):        # initialize fixed sized weatherApp instance        super(weatherApp, self).__init__()        uic.loadUi('forms/mainWindow.ui', self).setFixedSize(800, 600)        # display correct days        self.labelToday.setText(getDay().strftime("%A"))        self.labelTomorrow.setText(getDay(daysFromNow=1).strftime("%A"))        self.labelDayAfter.setText(getDay(daysFromNow=2).strftime("%A"))        # call onClick function on button click        self.city = ''        self.submitCity.clicked.connect(self.onClick)    def displayWeather(self, weather):        # display labels with current city temperature, weather description with icons        self.cityTemp.setText('{}℃'.format(weather[0]))        self.weatherDescription.setText(weather[3])        self.todaysForecastTemp.setText('{} / {}℃'.format(weather[1], weather[2]))        self.todaysForecastDesc.setText(weather[4])        icon = QPixmap(getWeatherIcon(weather[5]))        self.todaysIcon.setPixmap(icon)        # display tomorrow's forecast with icons        weather = getWeatherForecastByDay(self.city, daysFromNow=1)        self.tomoForecastTemp.setText('{} / {}℃'.format(weather[0], weather[1]))        self.tomoForecastDesc.setText(weather[2])        icon = QPixmap(getWeatherIcon(weather[3]))        self.tomoIcon.setPixmap(icon)        # display day after tomorrow's forecast with icons        weather = getWeatherForecastByDay(self.city, daysFromNow=2)        self.dayAftersForecastTemp.setText('{} / {}℃'.format(weather[0], weather[1]))        self.dayAftersForecastDesc.setText(weather[2])        icon = QPixmap(getWeatherIcon(weather[3]))        self.dayAftersIcon.setPixmap(icon)    def onClick(self):        # get weather report for given city        self.city = self.inputCity.text()        weather = getCurrentWeather(self.city)        # 'invalid input' handling        if weather is None:            QtWidgets.QMessageBox.about(self, "Can't reach weather report", "Please try again")        else:            # update window title with city name            self.setWindowTitle("weatherApp - {}".format(self.city))            # display weather forecast on weatherApp window            self.displayWeather(weather)def main():    app = QtWidgets.QApplication([])    weatherAppWindow = weatherApp()    weatherAppWindow.show()    sys.exit(app.exec())if __name__ == '__main__':    main()